INSERT INTO `books`(`id`, `name`, `author`, `category`, `type`, `publisher`, `publisher_date`, `words_num`, `introduction`, `thumbnail_url`, `txt_url`, `audio_url`, `visits`, `price`, `was_price`, `rating`, `createdAt`, `updatedAt`, `version`) VALUES ('26aaef1f-e6a5-40b6-ad16-c2ff1aa25641', '1极简人类史：从宇宙大爆炸到21世纪', '[美]大卫·克里斯蒂安', '历史小说', 1, NULL, NULL, NULL, '《极简人类史》是一部视野宏大、脉络清晰的人类简史。它讲述现代智人近 10 万年的发展轨迹，以宇宙大爆炸为起点，在 138 亿年宇宙演化的壮阔背景下，俯瞰人类历史从无到有的全过程，构建一幅关于人类历史的大图景。', NULL, NULL, NULL, NULL, NULL, NULL, 7.4, 1522481996336, 1522481996336, 0);
INSERT INTO `books`(`id`, `name`, `author`, `category`, `type`, `publisher`, `publisher_date`, `words_num`, `introduction`, `thumbnail_url`, `txt_url`, `audio_url`, `visits`, `price`, `was_price`, `rating`, `createdAt`, `updatedAt`, `version`) VALUES ('382cb892-ebb9-4a1b-8a64-fa92d7db9f72', '2刻意练习', '[美] 安德斯·艾利克森（AndersEricsson）罗伯特·普尔（Robert Pool）', '经济管理', 1, NULL, NULL, NULL, '首次提出“刻意练习”这个概念的是佛罗里达州立大学（Florida State University）心理学家 K. Anders Ericsson。这套练习方法的核心假设是，专家级水平是逐渐地练出来的，而有效进步的关键在于找到一系列的小任务让受训者按顺序完成。这些小任务必须是受训者正好不会做，但是又正好可以学习掌握的。完成这种练习要求受训者思想高度集中，这就与那些例行公事或者带娱乐色彩的练习完全不同。“刻意练习”的理论目前已经被广泛接受。', NULL, NULL, NULL, NULL, NULL, NULL, 9, 1522481996337, 1522481996337, 0);
INSERT INTO `books`(`id`, `name`, `author`, `category`, `type`, `publisher`, `publisher_date`, `words_num`, `introduction`, `thumbnail_url`, `txt_url`, `audio_url`, `visits`, `price`, `was_price`, `rating`, `createdAt`, `updatedAt`, `version`) VALUES ('5cc1e0d1-d30d-4bb9-ad1c-86eeaa4bf103', '3金庸作品集：鹿鼎记', '金庸', '科幻武侠', 1, NULL, NULL, NULL, '《金庸作品集:鹿鼎记(共5册)》这部小说讲的是一个从小在扬州妓院长大的小孩韦小宝，他以不会任何武功之姿态闯江湖各大帮会、周旋皇帝朝臣之间并奉旨远征云南、俄罗斯之故事，书中充满精彩绝倒的对白及逆思考的事件，多读几遍搞不好可以仙福永享寿与天齐呢！', NULL, NULL, NULL, NULL, NULL, NULL, 8.1, 1522481996338, 1522481996338, 0);
INSERT INTO `books`(`id`, `name`, `author`, `category`, `type`, `publisher`, `publisher_date`, `words_num`, `introduction`, `thumbnail_url`, `txt_url`, `audio_url`, `visits`, `price`, `was_price`, `rating`, `createdAt`, `updatedAt`, `version`) VALUES ('df9ed93f-9811-48a2-8c3b-59b3d91ba434', '4毛泽东传', '罗斯·特里尔', '人物传记', 1, NULL, NULL, NULL, '《毛泽东传》是2006年1月中国人民大学出版社出版的图书，作者是罗斯·特里尔。本书主要从思想、政治角度记叙毛泽东一生的角度，保持了优美流畅、生动自如的风格，对历史事件的描述更为敏锐新颖，更多地关注毛泽东的个人生活、个性性格和心理分析，对于人物和情景的分析更为出神入化，引导读者体味“故事”背后的时代困惑，并且据此更为具体地描绘出了他脑海中想象的“毛泽东的真实的画像”。', NULL, NULL, NULL, NULL, NULL, NULL, 8.5, 1522481996339, 1522481996339, 0);
INSERT INTO `books`(`id`, `name`, `author`, `category`, `type`, `publisher`, `publisher_date`, `words_num`, `introduction`, `thumbnail_url`, `txt_url`, `audio_url`, `visits`, `price`, `was_price`, `rating`, `createdAt`, `updatedAt`, `version`) VALUES ('df9ed93f-9811-48a2-8c3b-59b3d91ba43a', '5琅琊榜', '海宴', '青春言情', 1, NULL, NULL, 736000, '天下第一大帮江左盟的宗主梅长苏化名苏哲来到帝都金陵，同时也带来了一场场风波与一团团迷雾。具有双重身份的贵公子，争嫡中的太子与亲王，手握禁军的国中第一高手，统率南境铁骑的美丽郡主……周旋在这些权高位重身份不凡的人之间，为什么反而是行事低调的苏哲成为了所有事件的核心？掀起狂风巨涛的那只幕后之手，会不会就是前一波恶浪席卷之后留下的泡沫？他的身份，究竟是雪夜薄甲逐敌千里的少年将军，还是病体支离年寿难永的阴沉谋士？他重回旧地再遇旧友，他翻云覆雨机关算尽，到底是想要达到一个什么样的目的？', 'https://gss1.bdstatic.com/9vo3dSag_xI4khGkpoWK1HF6hhy/baike/c0%3Dbaike72%2C5%2C5%2C72%2C24/sign=61ef07bd4e086e067ea5371963611091/d4628535e5dde7114999d87aa5efce1b9c1661f7.jpg', NULL, NULL, 453000, 0, 0, 9.7, 1522481996340, 1522481996340, 0);
INSERT INTO `books`(`id`, `name`, `author`, `category`, `type`, `publisher`, `publisher_date`, `words_num`, `introduction`, `thumbnail_url`, `txt_url`, `audio_url`, `visits`, `price`, `was_price`, `rating`, `createdAt`, `updatedAt`, `version`) VALUES ('df9ed93f-9811-48a2-8c3b-59b3d91ba43b', '6三体', '刘慈欣', '科幻武侠', 1, NULL, NULL, 1334992, '作者试图讲述一部在光年尺度上重新演绎的中国现代史，讲述一个文明二百次毁灭与重生的传奇。小说《三体》与三体问题有关，其中描述了一种在半人马座三星生存的三体人及其三体文明。同时《三体》也是小说中的一个模拟三体文明在一个有三颗太阳的星系中挣扎生存并发展的网络游戏，应该是由希望三体文明降临地球介入人类文明的三体组织开发的。', 'https://gss3.bdstatic.com/-Po3dSag_xI4khGkpoWK1HF6hhy/baike/c0%3Dbaike220%2C5%2C5%2C220%2C73/sign=8ff963cdcbbf6c81e33a24badd57da50/c9fcc3cec3fdfc03813e641dd33f8794a5c226d4.jpg', NULL, NULL, 19302833, 53, 69.9, 9.8, 1522481996341, 1522481996341, 0);
