INSERT INTO `books`(`id`, `name`, `author`, `category`, `type`, `publisher`, `publisher_date`, `words_num`, `introduction`, `thumbnail_url`, `txt_url`, `audio_url`, `visits`, `price`, `was_price`, `rating`, `createdAt`, `updatedAt`, `version`) VALUES ('26aaef1f-e6a5-40b6-ad16-c2ff1aa25613', '19极简人类史：从宇宙大爆炸到21世纪', '[美]大卫·克里斯蒂安', '历史小说', 1, NULL, NULL, NULL, '《极简人类史》是一部视野宏大、脉络清晰的人类简史。它讲述现代智人近 10 万年的发展轨迹，以宇宙大爆炸为起点，在 138 亿年宇宙演化的壮阔背景下，俯瞰人类历史从无到有的全过程，构建一幅关于人类历史的大图景。', NULL, NULL, NULL, NULL, NULL, NULL, 7.4, 1522481996354, 1522481996354, 0);
INSERT INTO `books`(`id`, `name`, `author`, `category`, `type`, `publisher`, `publisher_date`, `words_num`, `introduction`, `thumbnail_url`, `txt_url`, `audio_url`, `visits`, `price`, `was_price`, `rating`, `createdAt`, `updatedAt`, `version`) VALUES ('382cb892-ebb9-4a1b-8a64-fa92d7db9f14', '20刻意练习', '[美] 安德斯·艾利克森（AndersEricsson）罗伯特·普尔（Robert Pool）', '经济管理', 1, NULL, NULL, NULL, '首次提出“刻意练习”这个概念的是佛罗里达州立大学（Florida State University）心理学家 K. Anders Ericsson。这套练习方法的核心假设是，专家级水平是逐渐地练出来的，而有效进步的关键在于找到一系列的小任务让受训者按顺序完成。这些小任务必须是受训者正好不会做，但是又正好可以学习掌握的。完成这种练习要求受训者思想高度集中，这就与那些例行公事或者带娱乐色彩的练习完全不同。“刻意练习”的理论目前已经被广泛接受。', NULL, NULL, NULL, NULL, NULL, NULL, 9, 1522481996355, 1522481996355, 0);
INSERT INTO `books`(`id`, `name`, `author`, `category`, `type`, `publisher`, `publisher_date`, `words_num`, `introduction`, `thumbnail_url`, `txt_url`, `audio_url`, `visits`, `price`, `was_price`, `rating`, `createdAt`, `updatedAt`, `version`) VALUES ('5cc1e0d1-d30d-4bb9-ad1c-86eeaa4bf115', '21金庸作品集：鹿鼎记', '金庸', '科幻武侠', 1, NULL, NULL, NULL, '《金庸作品集:鹿鼎记(共5册)》这部小说讲的是一个从小在扬州妓院长大的小孩韦小宝，他以不会任何武功之姿态闯江湖各大帮会、周旋皇帝朝臣之间并奉旨远征云南、俄罗斯之故事，书中充满精彩绝倒的对白及逆思考的事件，多读几遍搞不好可以仙福永享寿与天齐呢！', NULL, NULL, NULL, NULL, NULL, NULL, 8.1, 1522481996356, 1522481996356, 0);
INSERT INTO `books`(`id`, `name`, `author`, `category`, `type`, `publisher`, `publisher_date`, `words_num`, `introduction`, `thumbnail_url`, `txt_url`, `audio_url`, `visits`, `price`, `was_price`, `rating`, `createdAt`, `updatedAt`, `version`) VALUES ('df9ed93f-9811-48a2-8c3b-59b3d91ba416', '22毛泽东传', '罗斯·特里尔', '人物传记', 1, NULL, NULL, NULL, '《毛泽东传》是2006年1月中国人民大学出版社出版的图书，作者是罗斯·特里尔。本书主要从思想、政治角度记叙毛泽东一生的角度，保持了优美流畅、生动自如的风格，对历史事件的描述更为敏锐新颖，更多地关注毛泽东的个人生活、个性性格和心理分析，对于人物和情景的分析更为出神入化，引导读者体味“故事”背后的时代困惑，并且据此更为具体地描绘出了他脑海中想象的“毛泽东的真实的画像”。', NULL, NULL, NULL, NULL, NULL, NULL, 8.5, 1522481996357, 1522481996357, 0);
